    \concepttable{
        \conrow{形成条件}{$E_{Fm} > E_{FN}$}
        \conrow{载流子输运}{电子 $M \to S$}
        \conrow{弯曲}{能带\red{向下}弯曲}
        \conrow{表面}{积累层 ($n_s \gg n_0$)}
    }
