+        \conrow{肖特基势垒}{$e\phi_{\mathrm{B0}} = e\phi_{\mathrm{m}} - \chi$}
+        \conrow{内建电势}{$V_{bi} = \phi_{\mathrm{m}} - \phi_{\mathrm{s}} = \phi_{\mathrm{B0}} - \phi_n$}
+    }
+\end{minipage}
